(1, 1, '2014-11-18 20:20:05', '2014-11-18 20:20:05', 'Welcome to our website', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2015-01-06 20:12:08', '2015-01-06 20:12:08', '', 0, 'http://localhost:4567/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2014-12-27 20:20:05', '2014-12-27 20:20:05', 'We like to present you our new Software Trackstar. It is your solution for every team tracking of tasks problem.\r\n\r\n&nbsp;\r\n<blockquote><a href="http://localhost:4567/wordpress/wp-content/uploads/2014/11/trackStarProjects.jpg"><img class="alignnone size-medium wp-image-5" src="http://localhost:4567/wordpress/wp-content/uploads/2014/11/trackStarProjects-300x152.jpg" alt="trackStarProjects" width="300" height="152" /></a>\r\n\r\n&nbsp;\r\n\r\nFeatures:\r\n\r\n- Easy User Management\r\n\r\n- Easy Task Management\r\n\r\n- Easy archiving of tasks</blockquote>', 'All about Trackstar', '', 'publish', 'open', 'open', '', 'sample-page', '', '', '2015-01-06 17:59:35', '2015-01-06 17:59:35', '', 0, 'http://localhost:4567/wordpress/?page_id=2', 0, 'page', '', 0),
(3, 1, '2014-11-18 20:20:16', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2014-11-18 20:20:16', '0000-00-00 00:00:00', '', 0, 'http://localhost:4567/wordpress/?p=3', 0, 'post', '', 0),
(4, 1, '2014-12-31 15:07:05', '2014-12-31 15:07:05', 'We like to present you our new Software Trackstar. It is your solution for every team tracking of tasks problem.\n\n&nbsp;\n<blockquote><a href="http://localhost:4567/wordpress/wp-content/uploads/2014/11/trackStarProjects.jpg"><img class="alignnone size-medium wp-image-5" src="http://localhost:4567/wordpress/wp-content/uploads/2014/11/trackStarProjects-300x152.jpg" alt="trackStarProjects" width="300" height="152" /></a>\n\n&nbsp;\n\nFeatures:\n\n- Ea User Management\n\n&nbsp;</blockquote>', '', '', 'inherit', 'open', 'open', '', '2-autosave-v1', '', '', '2014-12-31 15:07:05', '2014-12-31 15:07:05', '', 2, 'http://localhost:4567/wordpress/?p=4', 0, 'revision', '', 0),
